INSERT INTO travel_agency.review (r_id, content, user_id, tour) VALUES (5, 'проживали в гостинице пару дней проездом(номер 12)остались осень довольны: большой номер, балкон, на окнах сетка от насекомых, чистая ванная комната с просторной душевой кабиной(хороший напор воды)фен, много всяких крючков и вешалок, много разеток, небольшой холодильник, хороший завтрак, приятный персонал, вечером есть место na парковке без проблем, Метцинген аутлет в 10км. Если будем в тех краях, обязательно вернемся! Рекомендую', 3, 2);
